import React from 'react'
import { useForm } from "react-hook-form";
export default function TaskHookForm({ kisiler, submitFn }) {
 const {
  registir,
  handleSubmit,
  formState:{errors}

  } =useForm({
  mode:"onChange",

 });
 const theSubmit=(data)=>console.log(data);
 return(
  <form onSubmit={handleSubmit(theSubmit)}> 
  <div className="form-line">
        <label className="input-label" htmlFor="title">
          Başlık
        </label>
        <input
          className="input-text"
          id="title"
         {...registir("title")}
          type="text"
         
         
        />
        <p className="input-error">{errors.title}</p>
      </div>

      <div className="form-line">
        <label className="input-label" htmlFor="description">
          Açıklama
        </label>
        <textarea
          className="input-textarea"
          rows="3"
          id="description"
         {...registir("description")}
         
          
        ></textarea>
        <p className="input-error">{errors.description}</p>
      </div>

      <div className="form-line">
        <label className="input-label">İnsanlar</label>
        <div>
          {kisiler.map((p) => (
            <label className="input-checkbox" key={p}>
              <input
                type="checkbox"
               {...registir("people")}
                value={p}
                
               
              />
              {p}
            </label>
          ))}
        </div>
        <p className="input-error">{errors.people}</p>
      </div>

      <div className="form-line">
        <button
          className="submit-button"
          type="submit"
          // disabled={buttonDisabled}
        >
          Kaydet
        </button>
      </div>
  </form>
 )
   
}
